<?php include "../../config/config_loading.php";include "../../config/config_all.php"; ?><!DOCTYPE html><html lang="en"><head> <meta charset="UTF-8"> <meta name="viewport" content="width=device-width, initial-scale=1.0"> <meta http-equiv="X-UA-Compatible" content="ie=edge"> <title>Kas Rt</title> <link rel="stylesheet" href="../../script/bootstrap/css/bootstrap.min.css"> <script src="../../script/jquery/jquery.js"></script> <script src="../../script/bootstrap/js/bootstrap.min.js"></script> <script src="../../script/mobile/jquery.touchSwipe.min.js"></script><style>body{font-family: arial;}.divbody{width: 100%; margin: auto;}.output-table{width: 800px; margin: auto;}.table{width: 95%; margin: auto; overflow: auto;}.divbodyinner{position: fixed; top: 0px; bottom: 0px; left: 0px; right: 0px; width: 100%; height: 100%; background-color: rgba(38, 38, 38, 0.9);}.form-buat{width: 430px; margin: auto; margin-top: 100px; background-color: rgb(38, 38, 38); padding: 20px 10px; border-radius: 20px;}.btn{margin: 1px;}#divinneroutput{width: 100%; margin: auto; color: white;}.divinnerjquery{width: 440px; margin: auto; margin-top: 100px; background-color: rgb(38, 38, 38); color: white; padding: 20px 10px; border-radius: 10px;}.form-control{margin-bottom: 10px;}.divinnerdiv{width: 430px; margin: auto; margin-top: 100px;}.div-input{width: 650px; background-color: rgb(38, 38, 38); padding: 30px 25px; border-radius: 20px;}.divtd1{width: 100%; float: left; text-align: right;}.divtd2{width: 100%; float: left;}.divtd3{width: 34%; float: left;}.divtd4{width: 6%; float: left; text-align: center;}.divtd5{width: 60%; float: left;}@media screen and (max-width: 800px){.divbody{width: 100%;}.output-table{width: 97%;}}@media screen and (max-width: 700px){.div-input{width: 97%;}.divinnerdiv{width: 98%;}}@media screen and (max-width: 500px){.form-buat{width: 97%;}.divinnerjquery{width: 97%;}}</style></head><body><div class="divbody"> <h1 style="text-align: center;">Kas Rt</h1></div><div class="output-table"> <div id="accountgrupnotif"> </div><br><br><table class="table table-striped" id="output"> </table> <br><br><br><br><?php if($status=="bendahara"){?> <div class=""> <button style="position: fixed;right: 20px;bottom: 40px;box-shadow: 0px 0px 10px #333333;" type="button" class="btn btn-dark" id="btn-click" btnaction="form-buat" onclick="openform();">Buat Baru</button> </div><?php}?></div></div><div class="divbodyinner" style="display: none;overflow: auto;"> <div id="close-divbodyinner" style="position: fixed;top: 5px;right: 17px;font-size: 30px;color: white;"><button onclick="closeform();openiklan();" id="btn-click" class="btn btn-danger">X</button></div><div class="form-buat div-input" style="display: none;"> <form method="post" id="form_buat"> <input class="form-control" type="text" name="nama" placeholder="Nama Kas" required><br></form> <div style="text-align: right;"><button id="btn-buat" class="btn btn-success">Buat</button></div></div><div class="divinnerdiv" style="display: none;"> <?php if($status=="bendahara"){?> <button style="position: fixed;right: 20px;bottom: 40px;" id="btn-buat-kas" kascode="" class="btn btn-success">Tambah</button> <?php}?> <table style="margin-bottom: 100px;font-size: 18px;" class="table table-dark" id="divinneroutput"> </table> </div><div class="divinnerkas divinnerjquery" style="display: none;"> <div><button id="submit-kas-back" class="btn btn-default">Kembali</button></div><br><br><input id="inputkas_keperluan" type="text" class="form-control" placeholder="Keperluan / Kegiatan"> Isi Salah Satu <input id="inputkas_pemasukan" type="text" class="form-control" placeholder="Pemasukan (Optional)"> <input id="inputkas_pengeluaran" type="text" class="form-control" placeholder="Pengeluaran (Optional)"> <label>Tanggal :</label> <input id="inputkas_tanggal" type="date" style="width: 150px;"> <div style="text-align: right;"><button id="submit-kas-cancel" kascode="" class="btn btn-danger">Batal</button> <button id="submit-kas" kascode="" class="btn btn-success">Simpan</button></div></div><div class="editinnerkas divinnerjquery" style="display: none;"> <input id="editkas_keperluan" type="text" class="form-control" placeholder="Keperluan / Kegiatan"><br>Isi Salah Satu<br><br>Pemasukan : <input id="editkas_pemasukan" type="text" class="form-control" placeholder="Pemasukan (Optional)"> Pengeluaran : <input id="editkas_pengeluaran" type="text" class="form-control" placeholder="Pengeluaran (Optional)"><br><label>Tanggal :</label> <input id="editkas_tanggal" type="date" style="width: 150px;"> <div style="text-align: right;"><button id="edit-inner-kas-cancel" class="btn btn-danger">Batal</button> </button><button id="edit-inner-kas" kasinner_code="" kascode="" class="btn btn-success">Simpan</button></div></div><div class="divdownload divinnerjquery" style="display: none;"> <div class="divdownloadas"> </div></div></div><div id="notification" class="bg-success" style="position: fixed;margin: auto;width: 250px;height: 100px;top: 0px;left: 0px;right: 0px;bottom: 0px;padding: 35px 0px;text-align: center;font-size: 20px;border-radius: 20px;z-index: 3;display: none;color: white;"></div><script>$(document).ready(function(){$("#notification").empty().html("Loading").fadeIn(); grup_code="<?php echo $grup; ?>"; $.ajax({url:"getdata.php", type:"POST", data:{grup_code:grup_code,}, dataType:"text", success:function(data){$("#notification").empty().fadeOut(); $("#output").append(data);}}); function getdata(){grup_code="<?php echo $grup; ?>"; $.ajax({url:"getdata.php", type:"POST", data:{grup_code:grup_code,}, dataType:"text", success:function(data){$("#output").empty().append(data);}});}$(document).on("click", "#btn-click", function(){var action=$(this).attr("btnaction"); if(action=="form-buat"){$(".form-buat").fadeIn("slow");}else{$(".form-buat").fadeOut(); $(".divinnerdiv").fadeOut(); $(".divinnerkas").fadeOut(); $(".editinnerkas").fadeOut(); $(".divdownload").fadeOut();}}); /* buat kas header (judul) */ $(document).on("click", "#btn-buat", function(){var data=$("#form_buat").serializeArray(); $.each(data, function(i, field){if(field.value==""){}else{$("#btn-buat").html("Loading.."); $.ajax({type:"post", url:"action.php", data:data, dataType:"text", success:function(data){$("#btn-buat").html("Buat"); $("#notification").empty().append(data).fadeIn("1000").delay("1500").fadeOut("slow"); $(".divbodyinner").fadeOut(); $(".form-buat").fadeOut(); $("#form_buat")[0].reset(); $(document).ready(function(){getdata(); qupdate();});}});}});}); /* data delete*/ $(document).on("click", "#headerdelete", function(){var headerdelete=$(this).attr("headerdeletedata"); $(this).html("loading"); $.ajax({type:"post", url:"action.php", data:{headerdelete:headerdelete}, dataType:"text", success:function(data){$("#notification").empty(); $("#notification").append(data); $("#notification").fadeIn().delay("700").fadeOut("slow"); $(document).ready(function(){getdata(); qupdate();});}});}); /* read kas inner*/ $(document).on("click", "#readkas", function(){var kascode=$(this).attr("kascode"); $("#btn-buat-kas").fadeIn(); $("#notification").html("Loading").fadeIn("1000"); $.ajax({method:"post", url:"getdata2.php", data:{kascode:kascode}, dataType:"text", success:function(data){$("#notification").html("Loading Complete").delay("700").fadeOut(); $("#divinneroutput").empty().append(data).fadeIn(); $(".divbodyinner").fadeIn(); $(".divinnerdiv").delay("700").fadeIn(); $("#btn-buat-kas").attr("kascode", kascode);}});}); $(document).on("click", "#btn-buat-kas", function(){var kascode=$(this).attr("kascode"); $("#divinneroutput").fadeOut(); $("#btn-buat-kas").fadeOut(); $(".divinnerkas").delay("700").fadeIn(); $("#submit-kas").attr("kascode", kascode); $("#submit-kas-cancel").attr("kascode", kascode); $("#submit-kas-back").attr("kascode", kascode);}); $(document).on("click", "#download", function(){var kascode=$(this).attr("kascode"); $(".divdownload").delay("700").fadeIn(); $(".divbodyinner").fadeIn(); $.ajax({method:"post", url:"save.php", data:{kascode:kascode,}, dataType:"text", success:function(data){$(".divdownload").empty().append(data);}});}); /* simpan kas inner */ $(document).on("click", "#submit-kas", function(){var keperluan=$("#inputkas_keperluan").val(); var pengeluaran=$("#inputkas_pengeluaran").val(); var pemasukan=$("#inputkas_pemasukan").val(); var tanggal=$("#inputkas_tanggal").val(); var kascode=$(this).attr("kascode"); $("#notification").empty().html("loading").fadeIn(); if(keperluan==""){$("#notification").fadeIn().html("keperluan Kosong").delay("1000").fadeOut();}else{$.ajax({type:"post", url:"action.php", data:{keperluan:keperluan, pengeluaran:pengeluaran, pemasukan:pemasukan, tanggal:tanggal, kascode:kascode, inputkasinner:"ya",}, dataType:"text", success:function(data){$("#notification").fadeIn().html(data).delay("700").fadeOut(); emptyval();}});}}); $(document).on("click", "#submit-kas-cancel", function(){var kascode=$(this).attr("kascode"); $(".divinnerkas").fadeOut(); $.ajax({method:"post", url:"getdata2.php", data:{kascode:kascode,}, dataType:"text", success:function(data){$("#divinneroutput").empty().append(data).delay("700").fadeIn(); $("#btn-buat-kas").delay().fadeIn();}});}); $(document).on("click", "#submit-kas-back", function(){var kascode=$(this).attr("kascode"); $(".divinnerkas").fadeOut(); $("#notification").empty().html("Loading").fadeIn(); $.ajax({method:"post", url:"getdata2.php", data:{kascode:kascode,}, dataType:"text", success:function(data){$("#divinneroutput").empty().append(data).delay("700").fadeIn(); $("#btn-buat-kas").delay().fadeIn(); $("#notification").empty().html("Loading Complete").delay("700").fadeOut();}});}); /* hapus kas inner */ $(document).on("click", "#hapus-kas-inner", function(){var kasinnercode=$(this).attr("kasinnercode"); var kascode=$(this).attr("kascode"); $("#notification").empty().html("loading").fadeIn(); $.ajax({type:"post", url:"action.php", data:{kasinnercode:kasinnercode, kasinnerdelete:"ya",}, dataType:"text", success:function(){$.ajax({method:"post", url:"getdata2.php", data:{kascode:kascode}, dataType:"text", success:function(data){$("#notification").empty().html("Berhasil").delay("700").fadeOut(); $("#divinneroutput").empty().append(data);}});}});}); /* edit kas inner */ $(document).on("click", "#btn-titlekas-edit", function(){$("#tdtitlekas").fadeOut("1000").empty(); var kascode=$(this).attr("kascode"); var titlekas=$(this).attr("titlekas"); $("#btn-simpantitlekas").attr("kascode", kascode); $("#inputtitlekas").val(titlekas); $("#tdformtitlekas").fadeIn("1000");}); $(document).on("click", "#btn-simpantitlekas", function(){var kascode=$(this).attr("kascode"); var titlekas=$("#inputtitlekas").val(); $.ajax({method:'post', url:'action.php', data:{titlekas:titlekas, kascode:kascode}, dataType:"text", success:function(data){$("#notification").empty().html("Berhasil").fadeIn().delay("700").fadeOut(); emptyval(); $.ajax({method:"post", url:"getdata2.php", data:{kascode:kascode}, dataType:"text", success:function(data){$("#divinneroutput").delay("700").fadeIn().empty().append(data);}});}});}); $(document).on("click", "#btn-edit-kas-inner", function(){var editkas_keperluan=$(this).attr("editkas_keperluan"); var editkas_pemasukan=$(this).attr("editkas_pemasukan"); var editkas_pengeluaran=$(this).attr("editkas_pengeluaran"); var editkas_tanggal=$(this).attr("editkas_tanggal"); var kasinner_code=$(this).attr("kasinner_code"); var kascode=$(this).attr("kascode"); $("#edit-inner-kas").attr("kasinner_code", kasinner_code).attr("kascode", kascode); $("#editkas_keperluan").val(editkas_keperluan); $("#editkas_pemasukan").val(editkas_pemasukan); $("#editkas_pengeluaran").val(editkas_pengeluaran); $("#editkas_tanggal").val(editkas_tanggal); $("#divinneroutput").fadeOut(); $("#btn-buat-kas").fadeOut(); $(".editinnerkas").delay("700").fadeIn();}); $(document).on("click", "#edit-inner-kas", function(){var keperluan=$("#editkas_keperluan").val(); var pemasukan=$("#editkas_pemasukan").val(); var pengeluaran=$("#editkas_pengeluaran").val(); var tanggal=$("#editkas_tanggal").val(); var kasinner_code=$("#edit-inner-kas").attr("kasinner_code"); var kascode=$(this).attr("kascode"); $("#notification").empty().html("Loading").fadeIn(); $.ajax({method:"post", url:"action.php", data:{keperluan:keperluan, pemasukan:pemasukan, pengeluaran:pengeluaran, tanggal:tanggal, kasinner_code:kasinner_code, editkasinner:"ya",}, dataType:"text", success:function(data){$("#btn-buat-kas").fadeIn(); $(".editinnerkas").fadeOut(); $("#notification").empty().html("Berhasil").fadeIn().delay("700").fadeOut(); emptyval(); $.ajax({method:"post", url:"getdata2.php", data:{kascode:kascode}, dataType:"text", success:function(data){$("#divinneroutput").delay("700").fadeIn().empty().append(data);}});}});}); $(document).on("click", "#edit-inner-kas-cancel", function(){$("#btn-buat-kas").fadeIn(); $(".editinnerkas").fadeOut(); $("#divinneroutput").delay("700").fadeIn(); $(document).ready(function(){emptyval();});});}); auto update data$(document).ready(function(){qupdate();});function qupdate(){$.ajax({url:"update.php", dataType:"text", success:function(data){$("#accountgrupnotif").empty().html(data);}});}function openform(){$(".divbodyinner").fadeIn();}function closeform(){$(".divbodyinner").delay("500").fadeOut();}function emptyval(){$("#inputkas_keperluan").val(""); $("#inputkas_pengeluaran").val(""); $("#inputkas_pemasukan").val(""); $("#inputkas_tanggal").val(""); $("#editkas_keperluan").val(""); $("#editkas_pemasukan").val(""); $("#editkas_pengeluaran").val(""); $("#editkas_tanggal").val("");}</script></body></html>